using System;using System.Collections.Generic;using UnityEngine;public class ListingsUI : MonoBehaviour{    [SerializeField] private Transform _notificationsRoot;        [SerializeField] private ListingNotificationUI _notificationUIPrefab;    [SerializeField] private ListingUI _listingPopup;    private readonly List<ListingUI> _instances = new();        private void Start()    {        BuildingManager.Instance.OnListingCreated += AddListing;    }    private void OnDestroy()    {        BuildingManager.Instance.OnListingCreated -= AddListing;    }     private void AddListing(BuildingListing listing)    {        var instance = Instantiate(_notificationUIPrefab, _notificationsRoot);        instance.Setup(listing);        instance.OnSelected += OnListingSelected;        _instances.Add(instance);        listing.OnRemoved += () =>        {            _instances.Remove(instance);            Destroy(instance.gameObject);        };    }    private void OnListingSelected(BuildingListing listing)    {        _listingPopup.Setup(listing);    }}